<?phpclass App{    static $db = null;    static function getDatabase(){        if (!self::$db) {            self::$db = new Database('root', '', 'energy');        }        return self::$db;    }    static function getAuth(){        return new Auth(Session::getInstance());    }    static function PanelNews($type_a, $title, $msg_news){        echo "        <div class='panel panel-danger'>            <div class='alert alert-$type_a' role='alert'>" . $title . "</div>                <div class='panel-body' style='text-align: center;'>                    " . $msg_news . "                </div>        </div>        ";    }    static function redirect($page){        header("Location: $page");        exit();    }}