<?phpclass Database{    private $pdo;    public function __construct($login, $password, $db_name, $host = 'localhost'){        $this->pdo = new PDO("mysql:dbname=$db_name;host=$host" , $login , $password);        $this->pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);        $this->pdo->setAttribute(PDO::ATTR_DEFAULT_FETCH_MODE, PDO::FETCH_OBJ);    }    /**     * @param $statement     * @param bool|array $params     * @return PDOStatement     */    public function query($statement, $params = false){        if ($params){            $req = $this->pdo->prepare($statement);            $req->execute($params);        }else{            $req = $this->pdo->prepare($statement);        }        return $req;    }    public function lastInsertId(){        return $this->pdo->lastInsertId();    }}