<?php    class valid{        private $data;        private $errors = [];        public function __construct($data){            $this->data = $data;        }        private function getField($field){            if (!isset($this->data[$field])){                return null;            }            return $this->data[$field];        }        public function IsAlpha($field, $errorMsg){            if (!preg_match('/^[a-zA-Z0-9_]+$/', $this->getField($field))){                $this->errors[$field] = $errorMsg;            }        }        public function IsUnique($field, $db, $table, $errorMsg){            $record_user = $db->query("SELECT id FROM $table WHERE $field = ?", [$this->getField($field)])->fetch();            if ($record_user){                $this->errors[$field] = $errorMsg;            }        }        public function IsEmail($field, $errorMsg){            if (!filter_var($this->getField($field), FILTER_VALIDATE_EMAIL)){                $this->errors[$field] = $errorMsg;            }        }        public function IsConfirmed($field, $errorMsg = ''){            if (empty($this->getField($field)) || $this->getField($field) != $this->getField($field . '_confirm')){                $this->errors[$field] = $errorMsg;            }        }        public function IsValid(){            return empty($this->errors);        }        public function getErrors(){            return $this->errors;        }    }